import React, { useEffect, useState } from 'react';
import { IChannel } from '@yomo/presence';
import { GroupHugProps } from './types';

export default function GroupHug(props: GroupHugProps) {
  const { id, avatar } = props;
  const [users, setUsers] = useState([]);
  const [channel, setChannel] = useState(null);
  useEffect(() => {
    let channel: IChannel | null = null;
    props.presence.then((yomo) => {
      channel = yomo.joinChannel('hug-group', {
        id,
        avatar,
      });

      channel.subscribePeers((peers) => {
        setUsers([{ id: id, avatar: avatar }, ...peers]);
      });
      setUsers([{ id: id, avatar: avatar }]);
      setChannel(channel);
    });

    window.addEventListener('beforeunload', () => {
      channel?.leave();
    });
  }, []);

  if (!channel) {
    return <div></div>;
  }

  return (
    <div className="flex items-center">
      <div
        className="relative flex "
        style={{
          marginRight: `${14 - Math.min(users.length, 6) * 2}px`,
        }}
      >
        {users.slice(0, 6).map((user, i) => {
          if (i < 5) {
            if (user.avatar) {
              return (
                <img
                  key={user.id}
                  style={{
                    transform: `translateX(${i * -2}px)`,
                    zIndex: `${users.length - i}`,
                    width: '22px',
                    height: '22px',
                    objectFit: 'contain',
                    borderRadius: '50%',
                  }}
                  src={user.avatar}
                  alt={user.id}
                />
              );
            }
            return (
              <svg
                key={user.id}
                style={{
                  transform: `translateX(${i * -2}px);z-index:${
                    users.length - i
                  }`,
                }}
                width="22"
                height="22"
                viewBox="0 0 22 22"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
                xmlnsXlink="http://www.w3.org/1999/xlink"
              >
                <circle
                  cx="11"
                  cy="11"
                  r="10.5"
                  fill="url(#pattern0)"
                  stroke="#604CFF"
                />
                <defs>
                  <pattern
                    id="pattern0"
                    patternContentUnits="objectBoundingBox"
                    width="1"
                    height="1"
                  >
                    <use
                      xlinkHref="#image0_351_157"
                      transform="scale(0.0238095)"
                    />
                  </pattern>
                  <image
                    id="image0_351_157"
                    width="42"
                    height="42"
                    xlinkHref="data:image/png;base64,"
                  />
                </defs>
              </svg>
            );
          } else {
            return (
              <div
                key={user.id}
                style={{
                  transform: `translateX(${i * -2}px)`,
                  zIndex: `${users.length - i}`,
                }}
                className="relative w-[22px] h-[22px]
          text-white text-[12px]
          font-[500] font-normal"
              >
                <span
                  className="absolute inline-flex items-center justify-center w-full h-full rounded-full"
                  style={{
                    background: 'rgba(0, 0, 0, 0.6)',
                  }}
                >
                  +{users.length - 5}
                </span>
                <svg
                  viewBox="0 0 22 22"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                  xmlns:xlink="http://www.w3.org/1999/xlink"
                >
                  <circle
                    cx="11"
                    cy="11"
                    r="10.5"
                    fill="url(#pattern0)"
                    stroke="#604CFF"
                  />
                  <defs>
                    <pattern
                      id="pattern0"
                      patternContentUnits="objectBoundingBox"
                      width="1"
                      height="1"
                    >
                      <use
                        xlink:href="#image0_351_157"
                        transform="scale(0.0238095)"
                      />
                    </pattern>
                    <image
                      id="image0_351_157"
                      width="42"
                      height="42"
                      xlink:href="data:image/png;base64,"
                    />
                  </defs>
                </svg>
              </div>
            );
          }
        })}
      </div>
      <div
        className="flex rounded-[1rem] py-1 px-2"
        style={{
          background:
            'linear-gradient(115.54deg, #7787FF 30.62%, #E080C9 83.83%)',
        }}
      >
        <span className="font-normal leading-4 text-[0.75rem] text-white mr-[2px]">
          live
        </span>
        <span className="flex justify-center items-center text-[0.75rem] text-[#604CFF] rounded-full w-4 h-4 bg-white">
          {users.length}
        </span>
      </div>
    </div>
  );
}
